/**
 *  Created by wangAlen on 2019-11-12 11:49
 */
import { mapState } from "vuex";
import draggable from "vuedraggable";
import {
  warningMsg,
  successMsg,
  uuid,
  jsonDeepClone,
  STATUS_CODE,
  regYuanToFen,
  errorMsg,
  regFenToYuan,
  toDecimal2
} from "@/commonutils/common";
import { createGoods, getGoodsInfo } from "./request";
import CollapseTransition from "element-ui/lib/transitions/collapse-transition";
import GoodsSpecifications from "../../goodsSpecifications/goodsSpecifications";
import editor from "@/components/editor";
import { getGoodsCategory } from "../../../goodsCategory/request";
import { reqGetGoodsGroup } from "../../../goodsGroup/request";
import imageUpload from "@/components/imageUpload";

export default {
  name: "CreateGoods",
  data() {
    /**
     * 
     * @param {Object} rule
     * @param {Object} value
     * @param {Object} callback
     * @returns {Object} callback
     */
    let validatePrice = (rule, value, callback) => {
      if (value === "") {
        callback(new Error(""));
      } else if (value <= 0 || !/^[0-9]+(.[0-9]{1,5})?$/.test(value)) {
        callback(new Error(""));
      } else if (value > 99999) {
        callback(new Error(""));
      } else {
        callback();
      }
    };
    let checkWeight = (rule, value, callback) => {
      if (!value) {
        return callback(new Error(""));
      }
      if (value <= 0 || !/^[0-9]+(.[0-9]{1,5})?$/.test(value)) {
        callback(new Error(""));
      } else {
        if (value > 9999) {
          callback(new Error(""));
        } else {
          callback();
        }
      }
    };
    let validateTags = (rule, value, callback) => {
      if (!value.length) {
        return callback(new Error(""));
      } else {
        callback();
      }
    };
    return {
      Draft: STATUS_CODE.goodsDraft,
      Release: STATUS_CODE.goodsRelease,
      goodsGroup: [],
      goodsCategoryList: [],
      show1: true,
      show2: true,
      show3: true,
      drag: false,
      transf: [
        {
          name: "",
          id: 1
        }
      ],
      form: {
        category: "", // 
        name: "", // 
        costPrice: "", // 
        showPrice: "", // 
        description: "", // 
        serviceContent: "", // 
        freightId: "", // 
        weight: "", // 
        temporaryImgList: [],
        imgList: [], // 
        tags: [], // 
        video: "", // 
        specificationsData: [], // 
        goodsInfo: "", // 
        goodsParams: [
          {
            key: "",
            value: ""
          },
          {
            key: "",
            value: ""
          }
        ],
        sku: []
      },
      loading: false,
      faceList: [],
      editorContent: "",
      FILE_TYPE_AVATAR: process.env.VUE_APP_FILE_TYPE_AVATAR,
      SUCC_CODE: process.env.VUE_APP_BASE_SUCC_CODE,
      ERR_TOKEN_CODE: process.env.VUE_APP_BASE_ERR_TOKEN_CODE,
      rules: {
        name: [
          {
            required: true,
            message: "",
            trigger: ["change"]
          },
          {
            min: 2,
            max: 50,
            message: " 2  50 ",
            trigger: "change"
          }
        ],
        category: [
          { required: true, message: "", trigger: "change" }
        ],
        freightId: [
          { required: true, message: "", trigger: "change" }
        ],
        costPrice: [
          {
            validator: validatePrice,
            trigger: "change"
          }
        ],
        showPrice: [
          {
            validator: validatePrice,
            trigger: "change"
          }
        ],
        tags: [
          {
            validator: validateTags,
            trigger: "change"
          }
        ],
        weight: [
          {
            validator: checkWeight,
            trigger: "change"
          }
        ]
      },
      isDraft: false,
      isRelease: false,
      goodsInfoOrigin: {},
      dialogVisible: false,
      isLoading: false,
      editLayout: {
        width: "100%",
        height: "400px"
      }
    };
  },
  // beforeRouteLeave(to, from, next) {
  //   this.$confirm("?", "", {
  //     confirmButtonText: "",
  //     cancelButtonText: ""
  //   })
  //     .then(() => {
  //       next();
  //     })
  //     .catch(() => {
  //       next(false);
  //     });
  // },
  components: {
    draggable,
    CollapseTransition,
    GoodsSpecifications,
    editor,
    imageUpload
  },
  created() {
    this.getGoodsInfoData();
    this.getGoodsCategoryList();
    this.getGoodsGroup();
  },
  computed: {
    ...mapState(["user", "aliyunOss"]),
    /**
     * 
     * @returns {boolean} 
     */
    isEdit() {
      return Boolean(this.routeData.query.goodsId);
    },
    /**
     * 
     * @returns {Route} 
     */
    routeData() {
      return this.$route;
    },
    /**
     * 
     * @returns {{ghostClass: string, disabled: boolean, animation: number, group: string}} 
     */
    dragOptions() {
      return {
        animation: 500,
        group: "description",
        disabled: false,
        ghostClass: "ghost"
      };
    },
    /**
     * 
     * @returns {Array} 
     */
    goodsParamsNew() {
      if (this.form.goodsParams.length >= 3) {
        return this.form.goodsParams.slice(2);
      } else {
        return [];
      }
    }
  },
  methods: {
    /**
     * id
     * @returns {Promise<void>} goodsInfo
     */
    async getGoodsInfoData() {
      const params = this.routeData.query;
      // 
      if (!params.goodsId) {
        // title
        // this.$route.meta.title = "";
        return;
      }
      this.isLoading = true;
      // this.$route.meta.title = "";
      // 
      const data = await getGoodsInfo({ id: params.goodsId });
      if (data.code !== STATUS_CODE.success) {
        errorMsg(this, data.msg);
        return;
      }
      this.goodsInfoOrigin = jsonDeepClone(data.result.goods); // 
      const goodsInfo = data.result.goods;
      const sku = [];
      const specificationsData = [];
      // 
      goodsInfo.options.list.forEach(item => {
        let arr = [];
        item.values.forEach(item => {
          arr.push({
            name: item,
            id: uuid()
          });
        });
        specificationsData.push({
          specifications: item.key,
          id: uuid(),
          type: arr
        });
      });
      // Sku
      goodsInfo.skus.forEach(item => {
        let obj = {};
        let uid = "";
        let valueList_ = [];
        // sku value listuidvalueList_
        item.options.list.forEach(item => {
          obj[item.key] = item.value;
          valueList_.push(item.value);
          uid += item.value + "-";
        });
        obj["showPrice"] = regFenToYuan(parseInt(item.showPrice)); // sku
        obj["costPrice"] = regFenToYuan(parseInt(item.costPrice)); // sku
        obj["count"] = parseInt(item.totalStockNum); // 
        obj["snCode"] = item.snCode;
        obj["cover"] = item.cover;
        obj["statusName"] = item.statusName;
        obj["status"] = item.status;
        obj["id"] = item.id;
        obj.uid = uid.substring(0, uid.length - 1);
        obj.valueList = valueList_; // valueList
        sku.push(obj);
      });
      const tags = () => {
        const arr = [];
        goodsInfo.tags.forEach(item => {
          arr.push(item.id);
        });
        return arr;
      }
      // 
      this.form = {
        category: goodsInfo.classId, // 
        description: goodsInfo.description, // 
        serviceContent: goodsInfo.serviceContent, // 
        freightId: parseInt(goodsInfo.freightId), // 
        weight: goodsInfo.weight, // 
        name: goodsInfo.name, // 
        showPrice: regFenToYuan(goodsInfo.showPrice), // 
        costPrice: regFenToYuan(goodsInfo.costPrice), // 
        imgList: goodsInfo.pictures.list, // 
        temporaryImgList: goodsInfo.pictures.list,
        video: goodsInfo.video, // 
        specificationsData: specificationsData, // 
        goodsParams: goodsInfo.extraInfo.list,
        tags: tags(),
        sku: sku
      };
      this.$set(this.form, "goodsInfo", goodsInfo.details); // 
      this.$set(this.form, "id", this.routeData.query.goodsId); // id
      this.isLoading = false;
    },
    /**
     * 
     * @returns {Promise<void>} 
     */
    async getGoodsCategoryList() {
      const data = await getGoodsCategory({
        isAll: true
      });
      if (data.code === STATUS_CODE.success) {
        // 0children
        const lookData = arr => {
          if (arr.length) {
            arr.forEach(item => {
              if (!item.children.length) {
                delete item.children;
              } else {
                lookData(item.children);
              }
            });
          }
        };
        lookData((this.goodsCategoryList = data.result.list));
      }
    },
    /**
     * 
     * @returns {Promise<void>} 
     */
    async getGoodsGroup() {
      const data = await reqGetGoodsGroup(undefined, undefined, true);
      if (data.code === STATUS_CODE.success) {
        this.goodsGroup = data.result.list;
      }
    },
    /**
     * 
     * @param {Object} val
     * @returns {void}
     */
    delGoodsParams(val) {
      this.form.goodsParams = this.form.goodsParams.filter(
        item => val.uid !== item.uid
      );
    },
    /**
     * 
     * @returns {void}
     */
    addGoodsParams() {
      this.form.goodsParams.push({
        uid: uuid(),
        key: "",
        value: ""
      });
    },
    /**
     * 
     * @param {String} goodsForm
     * @param {number} type
     * @returns {void}
     */
    async save(goodsForm, type) {
      this.form.type = type;
      const data = jsonDeepClone(this.form);
      const sku = this.$refs.specificationsData.tableData;
      const skuList = [];
      const options = [];
      data.specificationsData.forEach(item => {
        let arr = [];
        item.type.forEach(type => {
          arr.push(type.name);
        });
        options.push({
          key: item.specifications,
          values: arr
        });
      });
      // sku
      sku.forEach(item => {
        let optionList = [];
        let valueList = item.valueList;
        for (let key in item) {
          let obj = {};
          // optionskukeyoption
          valueList.forEach(ele => {
            // keyuidkey
            if (item[key] === ele && key !== "uid") {
              obj.key = key;
              obj.value = ele;
              optionList.push(obj);
            }
          });
        }
        skuList.push({
          showPrice: regYuanToFen(item.showPrice),
          costPrice: regYuanToFen(item.costPrice),
          totalStockNum: item.count,
          snCode: item.snCode,
          cover: item.cover,
          options: {
            list: optionList || []
          }
        });
      });
      // 
      const tags = val => {
        const arr = [];
        if (val && val.length) {
          this.goodsGroup.forEach(item => {
            if (val.indexOf(item.id) >= 0) {
              arr.push(item);
            }
          });
        }
        return arr;
      };
      // 
      const goodsParamsStatus = data.goodsParams.every(item => {
        return (
          item.value &&
          item.key &&
          item.value.length < 20 &&
          item.key.length < 20
        );
      });
      this.$refs[goodsForm].validate(async valid => {
        if (type === this.Draft && !data.name.length) {
          warningMsg(this, "");
          return false;
        } else if (type === this.Release) {
          if (!valid) {
            warningMsg(this, "");
            return false;
          }
          if (!this.form.imgList.length) {
            warningMsg(this, "");
            return false;
          }
          if (!this.form.specificationsData.length) {
            warningMsg(this, "");
            return false;
          }
          if (
            skuList.some(item => {
              return !item.snCode.trim();
            })
          ) {
            warningMsg(this, "");
            return false;
          }
          if (!data.freightId) {
            warningMsg(this, "");
            return false;
          }
          if (!sku.length) {
            warningMsg(this, "");
            return false;
          }
          if (!data.goodsInfo.trim().length) {
            warningMsg(this, "");
            return false;
          }
          if (!goodsParamsStatus) {
            warningMsg(this, "120");
            return false;
          }
        } else {
          // eslint-disable-next-line no-console
          console.info("", data);
        }
        type === this.Draft ? (this.isDraft = true) : (this.isRelease = true);
        const params = {
          goods: {
            id: data.id || undefined, // id
            name: data.name, // 
            classId: data.category, // 
            showPrice: regYuanToFen(toDecimal2(data.showPrice)), // ()
            costPrice: regYuanToFen(toDecimal2(data.costPrice)), // 
            pictures: {
              // 
              list: data.imgList || []
            },
            video: data.video || undefined, // 
            details: data.goodsInfo, // ()
            status: data.type, // .
            totalStockNum: data.totalStockNum, // 
            description: data.description, // 
            serviceContent: data.serviceContent, // 
            freightId: data.freightId, // 
            weight: parseInt(data.weight), // 
            tags: tags(data.tags), // 
            options: {
              // 
              list: options || []
            },
            extraInfo: {
              // 
              list: data.goodsParams || []
            },
            skus: skuList || [] // SKU
          }
        };
        const response = await createGoods(params);
        setTimeout(() => {
          this.isDraft = false;
          this.isRelease = false;
        }, 500);
        if (response.code !== STATUS_CODE.success) {
          errorMsg(this, response.msg);
          return;
        }
        successMsg(this, `${data.id ? "" : ""}`);
        await this.$router.replace(this.routeData.query.path_);
      });
    }
  }
};
